actions.script:
appCardList = []
def subscreens = currentScreenDef?.getMenuSubscreensItems()
if (subscreens) {
    subscreens.each { subsItem ->
        def screenDef = sri.sfi.getScreenDefinition(subsItem.location)
        String screenPath = "/apps/${subsItem.name}"
        def urlInstance = sri.buildUrl(screenPath)
        if (screenDef && !screenDef.getParameterMap() && urlInstance?.isPermitted()) {
            String title = ec.resource.expand(subsItem.menuTitle, "")
            String icon = urlInstance.sui.menuImage ?: "fa fa-link"
            String iconType = urlInstance.sui.menuImageType ?: "icon"
            appCardList.add([
                    title   : title,
                    icon    : icon,
                    iconType: iconType,
                    path    : urlInstance.path,
                    url     : urlInstance.url
            ])
        }
    }
}
